import React from "react";
import ".././App.css";

class Signup extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      register: {
        email: [],
        senha: []
      },
      items: [],
      password: [],
    };
  }

  register = (ev) => {
    ev.preventDefault()

    if(this.valueEmail !== '') {
      let newEmail = {
        text: this.valueEmail.value,
        key: Date.now()
      }
    }
  }

  render() {
    return (
      <form
        onSubmit={ev => {
          ev.preventDefault();
          this.props.handleSetState("login");
        }}
      >
        <label>
          Nome:
          <input type="text" placeholder="Nome" />
        </label>
        <label>
          Email:
          <input type="text" placeholder="Email" />
        </label>
        <label>
          Senha:
          <input type="text" placeholder="Senha" />
        </label>
        <input type="submit" />
      </form>
    );
  }
}

export default Signup;
