import React from "react";
import "./App.css";
import Login from "./components/Login";
import Signup from "./components/Signup";
import Timer from "./components/Timer";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      element: "login"
    };
  }

  renderElement = () => {
    switch (this.state.element) {
      case "login":
        return <Login />;
      case "signup":
        return <Signup />;
      case "timer":
        return <Timer />;
      default:
        return null;
    }
  };
