  //     case "timer":
  //       return <Timer />;
  //     default:
  //       return null;
  //   }
  // };

  handleSetState = tela => {
    this.setState({ element: tela });
  };

  render() {
    return <div className="App">{this.renderElement()}</div>;
  }
}

export default App;
