import React from "react";
import ".././App.css";

class Timer extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      timer: 0
    }
  }

  playTimer = () => {
    if (this.timer == null) {
      this.timer =setInterval(() => {
        this.state.timer += 1;
        this.setState(this.state);
      }, 1000);
    }
  }

  render() {
    return (
      <div>
        <h1>This page</h1>
        <form
          onSubmit={ev => {
            ev.preventDefault();
            this.props.handleSetState("login");
          }}
        >
          <input type="submit" value="Voltar" />
        </form>
      </div>
    );
  }
}

export default Timer;
